<div class="page-header"><h1>编辑 - <?php echo $product['Product']['name'];?></h1></div><div class="row">	<div class="span3">		<?php			$thumbnail = $this->Phpthumb->generate(				array(					'save_path' => WWW_ROOT . IMAGE_ROOT . 'thumbs',     					'display_path' => '/' . IMAGE_ROOT . 'thumbs',  					'error_image_path' => '/' . IMAGE_ROOT  . 'logo.jpg',					'src' => WWW_ROOT . IMAGE_ROOT . PRODUCT . $product['Product']['avatar'],					'w' => 200, 					'h' => 200,					'q' => 100,					'zc' => 1				)			);		?>		<?php echo $this->Html->image($thumbnail['src'], array('width' => $thumbnail['w'], 'height' => $thumbnail['h'])); ?>	</div>	<div class="span9">	<form class="form-horizontal" action="<?php echo $this->webroot?>dashboard/updateProduct" method="POST" enctype="multipart/form-data">		<table class="table table-striped">			<tr>				<th width=10%>产品图片</th>				<td><input type="file" class="input-file" id="avatar" name="avatar">				&nbsp;&nbsp;<span class="label label-important">如不需换产品图片，请勿选择!</span></td>			</tr>			<tr>				<th width=10%>产品名称</th>				<td><input type="text" class="input-xlarge" id="name" value="<?php echo $product['Product']['name'];?>" name="name"></td>			</tr>			<tr>				<th width=10%>产品介绍</th>				<td><textarea rows="8" id="intro" class="input-xxlarge" name="intro"><?php echo $product['Product']['intro'];?></textarea></td>			</tr>			<tr>				<th width=10%></th>				<td>					<input type="hidden" name="product_id" value="<?php echo $product['Product']['id'];?>">					<button class="btn btn-primary" type="submit" id="save">保存</button>				</td>			</tr>		</table>		</form>	</div></div><?php echo $this->Html->script(array('kindeditor/kindeditor')); ?><script>	var editor;	KindEditor.ready(function(K) {		editor = K.create('textarea[name="intro"]', {			resizeType : 1,			uploadJson : '<?php echo $this->webroot?>' + 'kindeditor/uploadJson',			fileManagerJson : '<?php echo $this->webroot?>' + 'kindeditor/fileManagerJson',			allowPreviewEmoticons : true,			allowFileManager : true,			items : [				 'fullscreen', '|','fontname', 'fontsize', '|', 'forecolor', 'hilitecolor', 'bold', 'italic', 'underline','removeformat', '|', 'justifyleft', 'justifycenter', 'justifyright', 'insertorderedlist','insertunorderedlist', '|', 'emoticons', 'image', 'link']		});	});</script><script type="text/javascript">$(function(){		$('#save').click(function(){		var checkStatus = true;				//验证产品名称有没有输入		if($.trim($('#name').val()) == ''){			alert('请输入产品名称');			checkStatus = false;			$('#name').focus();		}				//验证产品LOGO有没有		logoFile = $.trim($("#avatar").val());		logoFileArr = logoFile.split('.');		//alert(logoFileArr[1]);		if(logoFile != ''){			if(logoFileArr.length<2){				alert('文件格式错误');				checkStatus = false;			}else if(logoFileArr[1]!='jpg' && logoFileArr[1]!='png'){				alert('Logo只能是jpg或者png格式');				checkStatus = false;			}else{							}		} 				//验证产品介绍有没有输入		if($.trim($('#intro').val()) == ''){			alert('请输入产品介绍');			checkStatus = false;			$('#intro').focus();		}		return checkStatus;	});	});</script>