<div class="container">	<section id="adminmachinegroups">		<div style="overflow: hidden;">			<table class="table table-striped">				<?php foreach($news as $newItem):?>				<tr>					<td width=30%>						<h5>							<a href="<?php echo $this->Html->url(array("controller" => "dashboard","action" => "article", "?"=>array("article_id" => $newItem['Article']['id'])));?>">								<?php echo $newItem['Article']['title'];?>							</a>						</h5>					</td>					<td width=40%>						<?php echo $this->StringUtil->sysSubStr($newItem['Article']['content'], 200);?>					</td>					<td width=30%>						<ul class="unstyled">							<li>								<a href="<?php echo $this->Html->url(array("controller" => "dashboard","action" => "delArticle", "?"=>array("article_id" => $newItem['Article']['id'])));?>" class="btn-mini btn-danger">									删除新闻								</a>								&nbsp;								<a href="<?php echo $this->Html->url(array("controller" => "dashboard","action" => "editArticle", "?"=>array("article_id" => $newItem['Article']['id'])));?>" class="btn-mini btn-warning">									编辑新闻								</a>							</li>						</ul>					</td>				</tr>				<?php endforeach;?>			</table>			<p align='right'>				<a href="<?php echo $this->Html->url(array("controller" => "dashboard","action" => "addArticle", "?"=>array("brand_id" => $brand_id, 'type' => 'news')));?>" class="btn-small btn-success">					+新增一条新闻				</a>			</p>		</div>	</section></div>